import createDebugger       from 'core/debug/api/create'


const debug = createDebugger(`app:group:api:local:create`)

export default (payload) => {
  debug(`()`, payload)

  return {
    data: {
      [payload.id]: { 
        ...payload,

        imageLink: payload.imageLink || `https://i.imgur.com/H6GwuuM.jpg`,
      },
    },
    order: [payload.id],
  }
}