import React, { Component } from 'react'


import Line                 from '2d/line/web/Line'
import Texture              from '2d/texture/web/Texture'


class Group2d extends Component {
  render() {
    const {
      path,
      pathListForeground,
      perspectivePath,
      scale,
      type,
      imageLink,
    } = this.props

    const color = type === `foreground` ? 0xeeff00 : 0x0000ff
    const isShowTexture = type !== `foreground`

    return (
      <React.Fragment>
        <Line
          color={ color }
          path={[
            ...path,
            path[0],
          ]}
        />
        {
          isShowTexture && <Texture
            imageLink={ imageLink }
            path={ path }
            pathListForeground={ pathListForeground }
            perspectivePath={ perspectivePath }
            scale={ scale }
          />
        }
      </React.Fragment>
    )
  }
}

export default Group2d
