import React, { Component } from 'react'


import Line                 from '2d/line/web/Line'
import Texture              from '2d/texture/web/Texture'


class Group2d extends Component {
  render() {
    const { path } = this.props
    const { perspectivePath } = this.props
    const { imageLink } = this.props

    return (
      <React.Fragment>
        <Line
          path={[
            ...path,
            path[0],
          ]}
        />
        <Texture
          imageLink={ imageLink }
          path={ path }
          perspectivePath={ perspectivePath }
        />
      </React.Fragment>
    )
  }
}

export default Group2d
