import React, { Component } from 'react'


import GroupListItem        from 'app/group/web/GroupListItem'


class GroupList extends Component {
  render() {
    const { data } = this.props
    const { order } = this.props

    return order.map((id) => {
      const doc = data[id]

      return (
        <GroupListItem
          key={ id }
          {...doc}
        />
      )
    })
  }
}

export default GroupList