import React, { Component } from 'react'

import { Button }           from 'semantic-ui-react'


class GroupListItem extends Component {
  render() {
    const {
      name,
      type,
      onClick,
    } = this.props

    const isPrimary = type !== `foreground`

    return (
      <Button
        content={ name }
        primary={ isPrimary }

        onClick={ onClick }
      />
    )
  }
}

export default GroupListItem