import React, { Component } from 'react'

import { Button }           from 'semantic-ui-react'


class GroupListItem extends Component {
  render() {
    const { name } = this.props
    const { onClick } = this.props

    return (
      <Button
        content={ name }
        primary

        onClick={ onClick }
      />
    )
  }
}

export default GroupListItem