import React, { Component } from 'react'
import { withRouter }       from 'react-router-dom'


import EditorSection        from 'app/editor/web/EditorSection'
import ToolBarSection       from 'app/editor/web/ToolBarSection'
import GroupListSection     from 'app/group/web/GroupListSection'
import TextureListSection   from 'app/texture/web/TextureListSection'


const PageEditor = ({ history, match }) => {
  const { params = {} } = match
  const { id } = params

  return (
    <div>
      <h1>Редактор { id }</h1>
      <GroupListSection
        id={ id }
      />
      <EditorSection
        id={ id }
      />
      <ToolBarSection
        id={ id }
      />
      <TextureListSection
        id={ id }
      />
    </div>
  )
}

export default withRouter(PageEditor)