import modeMap              from 'app/project/api/modeMap'

import createDebugger       from 'core/debug/api/create'


const debug = createDebugger('app:project:api:local:create')

export default (payload) => {
  debug(`()`, payload)

  return {
    data: {
      [payload.id]: {
        scale: 1,
        mode: modeMap.DEFAULT,
        unit: 0,
        
        ...payload,
      },
    },
    order: [payload.id],
  }
}