import React, { Component } from 'react'


import ProjectCreate        from './ProjectCreate'
import ProjectListItem      from './ProjectListItem'


export default ({
  data,
  order,

  onAdd,
  onClick,
}) => {
  return (
    <div>
      <ProjectCreate
        onAdd={ onAdd } 
      />
      {order.map((id) => (
        <ProjectListItem
          {...data[id]}

          onClick={ onClick }  
        />
      ))}
    </div>
  )
}