import React, { Component } from 'react'

import styled               from 'styled-components'


const Project = styled.img`
  width: 200px;
  height: auto;

  border: 1px solid black;
`

export default ({
  id,
  imageLink,

  onClick,
}) => {
  return (
    <div>
      <Project
        src={ imageLink }

        onClick={() => onClick && onClick({ id })}
      />
    </div>
  )
}