import React, { Component } from 'react'


import projectQuery         from 'app/project/api/local/query'
import ProjectList          from 'app/project/web/ProjectList'

import Query                from 'core/query/web/Query'


export default ({
  onClick,
}) => {
  return (
    <div>
      <h2>Проекты</h2>
      <Query
        name='projectQuery'
        entity='project'

        query={ projectQuery }
      >
        {({ data, order }) => (
          <ProjectList
            data={ data }
            order={ order }

            onClick={ onClick }
          />
        )}
      </Query>
    </div>
  )
}