import { enable } 					from 'debug'
import Debug 								from 'debug'


try {
  if (window.localStorage.debug) {
    console.log(`core:debug:api:create - Enabling debug`, window.localStorage.debug)
    enable(window.localStorage.debug)
  }
} catch (e) {
  console.error(`core:debug:api:create - Could not enable debug`)
  console.error(e)
}

export default namespace => new Debug(`${ namespace }`)
