import createModule         from 'evokeme/redux/api/createModule'


import create               from './item/create'
import update               from './item/update'
import remove               from './item/remove'


export default () => {
  return {
    item: createModule({
      name: `item`,

      override: {
        create,
        update,
        remove,
      }
    }),
  }
}