import React, { Component } from 'react'


import Context              from 'pixi/all/Context'


const who = [`pixi`, `all`, `Provider`]

class Provider extends Component {
  constructor() {
    super()

    this.state = {
      app: null,

      debug: false,
    }
  }

  app = {
    set: ({ app }) => {
      this.state.debug && console.log(...who, `app`, `set`)

      this.setState({ app })
    },

    get: () => {
      this.state.debug && console.log(...who, `app`, `get`)

      return this.state.app
    },
  }

  render() {
    return (
      <Context.Provider value={{
        action: {
          app: this.app,
        },

        ...this.state,
      }}>
        { this.props.children }
      </Context.Provider>
    )
  }
}

export default Provider